sys_script_include.script:
var GetEmailAddress = Class.create();

// Extender a classe global.AbstractAjaxProcessor
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	//definir a função getEmail
	//Criar um GlideRecord para a tabela User
	//Usar o id passado no lado do cliente para recuperar um valor do banco de dados
	//Retornar o email correspondente
	getEmail: function(){
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},
	type: 'GetEmailAddress'
});
