sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		//Criando as variáveis date 
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//Se a data passada no argumento for anterior a agora, retorna true, caso contrário o retorno é false
		if(testDate.before(rightNow)){
			return true;
		}else{
			return false;
		}
	},

	isDateToday: function(strDate){
		//Criando as variáveis date
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//Fazer a verificação se a data passada é o dia de hoje
		if(testDate.getLocalDate() == rightNow.getLocalDate()){
			return true;
		}else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
